package assignment;

public class SwapTwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 6000;
		int b = 4000;
		b = a+b;
		a = b-a;
		b = b-a;
		System.out.println("after swapping");
		System.out.println("a is :" + a);
		System.out.println("b is :" + b);
		
	}

}
